-- ----------------------------
-- Triggers structure for table hq_application
-- ----------------------------
DROP TRIGGER IF EXISTS `application_acc`;
delimiter ;;
CREATE TRIGGER `application_acc` BEFORE UPDATE ON `hq_application` FOR EACH ROW BEGIN
	IF old.result = 0 AND new.result = 1 THEN 
		SET new.course_id =LAST_INSERT_ID();
END IF;
	IF old.executor != new.executor OR old.executor='' OR old.executor=NULL  THEN
		 update hq_course SET executor=new.executor WHERE hq_course.course_id=new.course_id;
		END IF;
END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table hq_course_situation
-- ----------------------------
DROP TRIGGER IF EXISTS `payment_init`;
delimiter ;;
CREATE TRIGGER `payment_init` BEFORE INSERT ON `hq_course_situation` FOR EACH ROW BEGIN
		DECLARE studentunit VARCHAR(255);
		DECLARE cst	DOUBLE;	
		DECLARE total	DOUBLE;
		SELECT unit INTO studentunit FROM hq_student WHERE student_id = new.cs_student_id;
		IF studentunit != '个人' THEN SET new.payment = 1;
		SELECT cost,total_income INTO cst,total FROM hq_course WHERE course_id = new.cs_course_id;
		UPDATE hq_course SET total_income = cst + total
		WHERE course_id = new.cs_course_id;
		END IF;
END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table hq_course_situation
-- ----------------------------
DROP TRIGGER IF EXISTS `unique_sc`;
delimiter ;;
CREATE TRIGGER `unique_sc` BEFORE INSERT ON `hq_course_situation` FOR EACH ROW BEGIN
    DECLARE scCount INT;


    SELECT COUNT(*) INTO scCount
    FROM hq_course_situation
    WHERE cs_student_id = NEW.cs_student_id and cs_course_id=NEW.cs_course_id;

    IF scCount > 0 THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'Duplicate course_situation. Insertion aborted.';
    END IF;
END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table hq_course_situation
-- ----------------------------
DROP TRIGGER IF EXISTS `add_income`;
delimiter ;;
CREATE TRIGGER `add_income` AFTER UPDATE ON `hq_course_situation` FOR EACH ROW BEGIN
	DECLARE cst	DOUBLE;	
	DECLARE total	DOUBLE;
	SELECT cost,total_income INTO cst,total FROM hq_course WHERE course_id = new.cs_course_id;
	IF old.payment = 0 AND new.payment = 1 THEN 
		UPDATE hq_course SET total_income = cst + total
		WHERE course_id = new.cs_course_id;
	END IF;
END
;;
delimiter ;

